import React from 'react';
import { RadioGroup, FormControlLabel, Radio } from '@mui/material';

const ModelSelection = ({ formData, setFormData }) => {
  const { vehicleType, vehicleModel } = formData;

  const vehicleModels = {
    Motorcycle: ['Honda', 'Yamaha', 'Suzuki'],
    Bicycle: ['Mountain Bike', 'Road Bike', 'Electric Bike'],
    Car: ['Toyota', 'Honda', 'Ford'],
    Truck: ['Ford F-150', 'Chevrolet Silverado', 'Dodge Ram'],
  };

  const handleModelChange = (selectedModel) => {
    setFormData((prevData) => ({
      ...prevData,
      vehicleModel: selectedModel // Update vehicleModel in formData
    }));
  };

  return (
    <div className="mb-4">
      <h3 className="text-lg font-medium mb-2">Select Vehicle Model:</h3>
      <RadioGroup
        value={vehicleModel}
        onChange={(e) => handleModelChange(e.target.value)}
      >
        {vehicleModels[vehicleType].map((model) => (
          <FormControlLabel
            key={model}
            value={model}
            control={<Radio />}
            label={model}
          />
        ))}
      </RadioGroup>
    </div>
  );
};

export default ModelSelection;
