import React from 'react';
import { RadioGroup, FormControlLabel, Radio } from '@mui/material';

const VehicleTypeSelection = ({ formData, setFormData }) => {
  const { numberOfWheels, vehicleType } = formData;
  useEffect(() => {
    const fetchData = async () => {
      try {
        const foodsData = await fetchTopFoods(); // Call fetchFoods to get data
        setFoods(foodsData);
        // setLoading(false); // Set loading to false after data is fetched
      } catch (error) {
        // Handle error if fetchFoods fails
        console.error('Error fetching foods:', error);
        // setLoading(false); // Set loading to false on error
      }
    };
    fetchData();
  }, []);


  // Determine vehicle types based on the number of wheels
  const vehicleTypes = numberOfWheels === '2' ? ['Motorcycle', 'Bicycle'] : ['Car', 'Truck'];

  const handleVehicleTypeChange = (selectedType) => {
    setFormData((prevData) => ({
      ...prevData,
      vehicleType: selectedType // Update vehicleType in formData
    }));
  };

  return (
    <div className="mb-4">
      <h3 className="text-lg font-medium mb-2">Select Vehicle Type üëáüèª</h3>
      <RadioGroup
        value={vehicleType}
        onChange={(e) => handleVehicleTypeChange(e.target.value)}
      >
        {vehicleTypes.map((type) => (
          <FormControlLabel
            key={type}
            value={type}
            control={<Radio />}
            label={type}
          />
        ))}
      </RadioGroup>
    </div>
  );
};

export default VehicleTypeSelection;
