import React from 'react';
import { TextField } from '@mui/material';

const NameInput = ({ formData, setFormData }) => {
  const handleNameChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  return (
    <>
      <div className='flex flex-col j gap-3 w-1/2'>
        <div>
          <TextField
            label="First Name"
            name="firstName" // Specify the name of the input field
            value={formData.firstName} // Access the specific field value
            onChange={handleNameChange} // Pass the handler directly
          />
          <TextField
            label="Last Name"
            name="lastName" // Specify the name of the input field
            value={formData.lastName} // Access the specific field value
            onChange={handleNameChange} // Pass the handler directly
          />
        </div>
      </div>
    </>
  );
};

export default NameInput;
