import React,{useState,useEffect} from 'react';
import { RadioGroup, FormControlLabel, Radio } from '@mui/material';
import axios from 'axios';
const WheelsSelection = ({ formData, setFormData }) => {

  const handleWheelChange = (newValue) => {
    setFormData((prevData) => ({
      ...prevData,
      numberOfWheels: newValue // Update numberOfWheels in formData
    }));
  };

  return (
    <>
      <h3 className="text-lg font-medium mb-2">Select Wheel Type üëáüèª</h3>

      <RadioGroup
        value={formData.numberOfWheels} // Set the value to the numberOfWheels field in formData
        onChange={(e) => handleWheelChange(e.target.value)} // Pass the selected value to handleWheelChange
      >
        <FormControlLabel value="2" control={<Radio />} label="2 Wheels" />
        <FormControlLabel value="4" control={<Radio />} label="4 Wheels" />
      </RadioGroup>
    </>
  );
};

export default WheelsSelection;
