import React,{useEffect, useState} from 'react';
import { RadioGroup, FormControlLabel, Radio } from '@mui/material';
import { fetchVehicleById, fetchVehicleTypes } from '../apis/apiCall';

const VehicleTypeSelection = ({ formData, setFormData }) => {
  const { numberOfWheels, vehicleType } = formData;

  const [vehicleTypeById, setVehicleTypeById] = useState([]); 


  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetchVehicleById(numberOfWheels); // Call fetchVehicleTypes to get data
        setVehicleTypeById(response.data)
        // setFormData(response);
      } catch (error) {
        console.error('Error fetching Bookings Data:', error);
      }
    };
    fetchData();
  }, []);


  const handleVehicleTypeChange = (selectedType) => {
    const selectedTypeName = vehicleTypes.find((type) => type.id === selectedType)?.name || '';

    setFormData((prevData) => ({
      ...prevData,
      vehicleType: selectedTypeName // Update vehicleType in formData
    }));
  };


  return (
    <div className="mb-4">
      <h3 className="text-lg font-medium mb-2">Select Vehicle Type üëáüèª</h3>
      <RadioGroup
        value={vehicleType}
        onChange={(e) => handleVehicleTypeChange(parseInt(e.target.value, 10))}
      >
        {vehicleTypeById.map((type) => (
          <FormControlLabel
            key={type}
            value={type}
            control={<Radio />}
            label={type.name}
          />
        ))}
      </RadioGroup>
    </div>
  );
};

export default VehicleTypeSelection;
