import React, { useState, useEffect } from 'react';
import { RadioGroup, FormControlLabel, Radio } from '@mui/material';
import { fetchVehicleById } from '../apis/apiCall';

const VehicleTypeSelection = ({ formData, setFormData }) => {
  const { numberOfWheels, vehicleType } = formData;
  const [vehicleTypesById, setVehicleTypesById] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetchVehicleById(numberOfWheels);
        setVehicleTypesById(response.data);
      } catch (error) {
        console.error('Error fetching Vehicle Types:', error);
      }
    };
    fetchData();
  }, [numberOfWheels]); // Add numberOfWheels to dependencies array to trigger fetch when it changes

  const handleVehicleTypeChange = (selectedType) => {
    setFormData((prevData) => ({
      ...prevData,
      vehicleType: selectedType
    }));
  };

  return (
    <div className="mb-4">
      <h3 className="text-lg font-medium mb-2">Select Vehicle Type üëáüèª</h3>
      <RadioGroup
        value={vehicleType}
        onChange={(e) => handleVehicleTypeChange(e.target.value)}
      >
        {/* Check if vehicleTypesById has data before mapping */}
        {vehicleTypesById.length > 0 ? (
          vehicleTypesById.map((type) => (
            <FormControlLabel
              key={type.id}
              value={type.name}
              control={<Radio />}
              label={type.name}
            />
          ))
        ) : (
          <FormControlLabel
            value=""
            control={<Radio disabled />}
            label="Loading..." // Show loading message while data is being fetched
          />
        )}
      </RadioGroup>
    </div>
  );
};

export default VehicleTypeSelection;
