import React,{useState,useEffect} from 'react';
import { RadioGroup, FormControlLabel, Radio } from '@mui/material';
import axios from 'axios';
const WheelsSelection = ({ formData, setFormData }) => {

  const handleWheelChange = (newValue) => {
    setFormData((prevData) => ({
      ...prevData,
      numberOfWheels: newValue // Update numberOfWheels in formData
    }));
  };


  const [vehicleTypes, setVehicleTypes] = useState([]);
  const [vehicleModels, setVehicleModels] = useState([]);

  // Function to fetch vehicle types based on number of wheels
  const fetchVehicleTypes = async (numberOfWheels) => {
    try {
      const response = await axios.get(`/vehicle/types?numberOfWheels=${numberOfWheels}`);
      setVehicleTypes(response.data);
    } catch (error) {
      console.error('Error fetching vehicle types:', error);
    }
  };

  // Function to fetch vehicle models based on selected vehicle type
  // const fetchVehicleModels = async (vehicleTypeId) => {
  //   try {
  //     const response = await axios.get(`/vehicle/models?vehicleTypeId=${vehicleTypeId}`);
  //     setVehicleModels(response.data);
  //   } catch (error) {
  //     console.error('Error fetching vehicle models:', error);
  //   }
  // };

  // Example usage: Fetch vehicle types when number of wheels changes
  useEffect(() => {
    fetchVehicleTypes(2); // Assuming default selection is 2 wheels
  }, []);

  return (
    <>
      <h3 className="text-lg font-medium mb-2">Select Wheel Type üëáüèª</h3>

      <RadioGroup
        value={formData.numberOfWheels} // Set the value to the numberOfWheels field in formData
        onChange={(e) => handleWheelChange(e.target.value)} // Pass the selected value to handleWheelChange
      >
        <FormControlLabel value="2" control={<Radio />} label="2 Wheels" />
        <FormControlLabel value="4" control={<Radio />} label="4 Wheels" />
      </RadioGroup>
    </>
  );
};

export default WheelsSelection;
